#!/usr/bin/env python3
import re
noNewLineRegex = re.compile('.*') #corresponderá a qualquer caractere até o caractere de quebra de linha
print(noNewLineRegex.search('Serve the public trust.\nProtect the innocent.\nUphold the law.').group())
print('\n\n')
newLineRegex = re.compile('.*',re.DOTALL) #re.DOTALL passado a re.compile corresponderá a todos os caracteres
print(newLineRegex.search('Serve the public trust.\nProtect the innocent.\nUphold the law.').group())
